import React from 'react';
import '../styles/main.scss';
import fetchShows from './services/FetchData.js';
import ShowList from './ShowList';
import ShowDetails from './ShowDetails';
import FilterCheckbox from './FilterCheckbox';
import FilterInput from './FilterInput';











class App extends React.Component {
  constructor(props) {
    super(props);
    this.handleCheckbox = this.handleCheckbox.bind(this);
    this.handleInput = this.handleInput.bind(this);
    this.renderShowDetails = this.renderShowDetails.bind(this);
    this.state={
      data:[],
      searchValue:'',
      checkValue: '',

      isRunnng: false

    }
  }

  componentDidMount(){
    fetchShows()
      .then(data => {
        this.setState ({
          data:data
        })
      })

  }

  handleCheckbox() {
    this.setState(prevState => {
      return {
        isRunning: !prevState.isRunning
      }
    })
  }

  handleInput() {}

  renderShowDetails(props){
    console.log(props)
    const urlId = props.match.params.id;
    const shows = this.state.data;
    for(let showObject of shows) {
      if(showObject.id === parseInt(urlId)) {
        return <ShowDetails show={showObject}/>
      }
    }
  }


  render() {
    const {data,isRunning} = this.state;
    return (
      <div className="App">
        <Switch>
          <Route exact path="/">
            <h1>Series</h1>
            <FilterInput handleInput={this.handleInput}
                         value={this.state.search}/>
            <FilterCheckbox handleCheckbox={this.handleCheckbox} isRunning={isRunning}/>
            {!data ?
            "estoy cargando..."
            :
            <ShowList isRunning={isRunning} 
                      dataList={this.state.data}
                      search={this.state.search}
                      isRunning={this.state.isRunning} />
            }
          </Route>
          <Route path="/card/:id" render={this.renderShowDetails}/>
        </Switch>

      </div>
  );
}
}


export default App;
