const FilterInput = (props) => {

  const updateInput = (event) => {
    props.handleInput(event.currentTarget.value);
  }

  return (
    <form className="form">
      <label htmlFor="search-input">Search:</label>
      <input  type="text"
              value={props.inputValue}
              onChange={updateInput}
              placeholder="Search" 
              className        
              />

    </form>

  )
}

export default FilterInput;
